import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import './ItemPage.scss';
import starIcon from '../../../images/star.svg'; // Переконайтеся, що шлях правильний
import { useDoctors } from '../../../hooks/useDoctors'; // Додайте цей імпорт

interface Doctor {
  id: string;
  name: string;
  picture: string;
  rating: number;
  location: string;
  description: string;
  price: number;
}

const ItemPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const { doctors } = useDoctors();
  const doctor = doctors.find(d => d.doctor_id === Number(id));

  if (!doctor) {
    return <div className='item-page'>Doctor not found</div>;
  }

  return (
    <div className='item-page'>
      <div className='info'>
        <img className='avatar' src={doctor.picture} alt={doctor.name} />
        <div className='details'>
          <div className='filters'>
            <div className='filter-item yellow'>
              {doctor.rating}
              <img width={20} height={20} src={starIcon} alt='stars' />
            </div>
            <div className='filter-item blue'>{doctor.location}</div>
          </div>
          <h1>{doctor.name}</h1>
          <h2>{doctor.description}</h2>
        </div>
      </div>

      <div className='price'>Price: {doctor.price} $</div>

      <div className='action-buttons'>
        <button className='book'>Book Appointment</button>
      </div>

      <div className='navigation'>
        <Link to='/catalog' className='blue-btn'>
          Go back
        </Link>
      </div>
    </div>
  );
};

export default ItemPage;
