import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import './ItemPage.scss';
import starIcon from '../../../assets/star-icon.svg'; // Переконайтеся, що шлях правильний

interface Doctor {
  id: string;
  name: string;
  picture: string;
  rating: number;
  location: string;
  description: string;
  price: number;
}

const ItemPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [doctor, setDoctor] = useState<Doctor | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        // Імітація запиту до API
        await new Promise(resolve => setTimeout(resolve, 1000));
        const fakeDoctor: Doctor = {
          id: id || '0',
          name: 'Dr. John Doe',
          picture: 'https://example.com/doctor-image.jpg',
          rating: 4.5,
          location: 'New York',
          description: 'Experienced cardiologist with 15 years of practice',
          price: 150
        };
        setDoctor(fakeDoctor);
      } catch (err) {
        setError('Failed to fetch doctor information');
      } finally {
        setLoading(false);
      }
    };

    fetchDoctor();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!doctor) return <div>Doctor not found</div>;

  return (
    <div className="item-page">
      <div className="info">
        <img className="avatar" src={doctor.picture} alt={doctor.name} />
        <div className="details">
          <div className="filters">
            <div className="filter-item yellow">
              {doctor.rating}
              <img width={20} height={20} src={starIcon} alt="stars" />
            </div>
            <div className="filter-item blue">{doctor.location}</div>
          </div>
          <h1>{doctor.name}</h1>
          <h2>{doctor.description}</h2>
        </div>
      </div>

      <div className="price">Price: ${doctor.price}</div>

      <div className="action-buttons">
        <button className="book">Book Appointment</button>
      </div>

      <div className="navigation">
        <Link to="/catalog" className="blue-btn">
          Back to Catalog
        </Link>
      </div>
    </div>
  );
};

export default ItemPage;
