import React, { FC, FormEvent } from 'react';
import './SectionMenu.css'; // або .scss, якщо ви використовуєте SCSS
import { IDoctor } from "../../../intefaces/doctorInterfaces";
import { useDoctors } from "../../context/DoctorsContext";

interface SectionMenuProps {
    doctors: IDoctor[];
    setDoctors: React.Dispatch<React.SetStateAction<IDoctor[]>>;
    setSearchOptions: React.Dispatch<React.SetStateAction<{
        term: string;
        sort: string;
        price: number;
        rating: number;
        country: string;
    }>>;
}

const SectionMenu: FC<SectionMenuProps> = ({ doctors, setDoctors, setSearchOptions }) => {
    const [active, setActive] = useState<boolean>(false);
    const [newDoctor, setNewDoctor] = useState<IDoctor>(defaultDoctor);
    const [error, setError] = useState<string>('');
    const [localSearchOptions, setLocalSearchOptions] = useState({
        term: '',
        price: '',
        rating: '',
        country: ''
    });

    const handleNewDoctor = (e: FormEvent) => {
        e.preventDefault();
        if (!newDoctor.name || !newDoctor.description || !newDoctor.price || !newDoctor.picture) {
            setError('All fields are required');
            return;
        }

        const isNameUnique = !doctors.some(doctor => doctor.name === newDoctor.name);
        if (!isNameUnique) {
            setError('Doctor name must be unique');
            return;
        }

        const maxId = doctors.length > 0 ? Math.max(...doctors.map(doctor => doctor.doctor_id)) : 0;
        setDoctors([...doctors, {...newDoctor, doctor_id: maxId + 1}]);
        setActive(false);
        setError('');
        setNewDoctor(defaultDoctor);

        console.log(newDoctor);
    }

    const handleSearch = (e: FormEvent) => {
        e.preventDefault();
        setSearchOptions({
            term: localSearchOptions.term,
            sort: '',
            price: Number(localSearchOptions.price) || 0,
            rating: Number(localSearchOptions.rating) || 0,
            country: localSearchOptions.country
        });
    }

    const handleClear = () => {
        setLocalSearchOptions({
            term: '',
            price: '',
            rating: '',
            country: ''
        });
        setSearchOptions({
            term: '',
            sort: '',
            price: 0,
            rating: 0,
            country: ''
        });
    }

    return (
        <section className="section-menu">
            <div className="create">
                <button className="create-button" onClick={() => setActive(true)}>Create a doctor</button>
            </div>
            <div className="search-menu" id="search-menu">
                <form className='filter-form' onSubmit={handleSearch}>
                    <label>
                        Price:
                        <select 
                            value={localSearchOptions.price}
                            onChange={(e) => setLocalSearchOptions(prev => ({...prev, price: e.target.value}))}>
                            <option value="">Select a price</option>
                            <option value="50">{'< '}50</option>
                            <option value="100">{'< '}100</option>
                            <option value="200">{'< '}200</option>
                            <option value="400">{'< '}400</option>
                        </select>
                    </label>
                    <label>
                        Rating:
                        <select 
                            value={localSearchOptions.rating}
                            onChange={(e) => setLocalSearchOptions(prev => ({...prev, rating: e.target.value}))}>
                            <option value="">Select a rating</option>
                            <option value="1">1+</option>
                            <option value="2">2+</option>
                            <option value="3">3+</option>
                            <option value="4">4+</option>
                        </select>
                    </label>
                    <label>
                        Country:
                        <select 
                            value={localSearchOptions.country}
                            onChange={(e) => setLocalSearchOptions(prev => ({...prev, country: e.target.value}))}>
                            <option value="">Select a country</option>
                            <option value="Ukraine">Ukraine</option>
                            <option value="Poland">Poland</option>
                            <option value="USA">USA</option>
                        </select>
                    </label>
                    <label className="input-buttons-menu">
                        <input 
                            placeholder="Type something..."
                            value={localSearchOptions.term}
                            onChange={(e) => setLocalSearchOptions(prev => ({...prev, term: e.target.value}))}
                        />
                    </label>
                    <div className="buttons-menu">
                        <button type="submit" className="search"><span>Search</span></button>
                        <button type="button" className="clear" onClick={handleClear}><span>Clear</span></button>
                    </div>
                </form>
            </div>

            <PopUpDoctorForm
                doctor={newDoctor}
                setDoctor={setNewDoctor}
                handleSubmit={handleNewDoctor}
                error={error}
                headText="Add new doctor"
                active={active}
                setActive={setActive}
            />
        </section>
    );
};

export default SectionMenu;
