import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { useDoctors } from '../../context/DoctorsContext';
import starIcon from '../../../images/star.png';
import './ItemPage.scss';

interface Item {
  id: string;
  name: string;
  description: string;
}

const ItemPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const { doctors } = useDoctors();
  const doctor = doctors.find((d) => d.doctor_id === Number(id));
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        // Тут має бути реальний запит до API
        // Для прикладу використовуємо фейкові дані
        await new Promise(resolve => setTimeout(resolve, 1000)); // Імітація затримки запиту
        if (!doctor) {
          throw new Error('Doctor not found');
        }
      } catch (err) {
        setError('Failed to fetch doctor information');
      } finally {
        setLoading(false);
      }
    };

    fetchDoctor();
  }, [id, doctor]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!doctor) return <div>Doctor not found</div>;

  return (
    <div className="item-page">
      <div className="info">
        <img className="avatar" src={doctor.picture} alt={doctor.name} />
        <div className="details">
          <div className="filters">
            <div className="filter-item yellow">
              {doctor.rating}
              <img width={20} height={20} src={starIcon} alt="stars" />
            </div>
            <div className="filter-item blue">{doctor.location}</div>
          </div>
          <h1>{doctor.name}</h1>
          <h2>{doctor.description}</h2>
        </div>
      </div>

      <div className="price">Price: {doctor.price} $</div>

      <div className="action-buttons">
        <button className="book">Book Appointment</button>
        <button className="contact">Contact</button>
      </div>

      <div className="navigation">
        <Link to="/catalog" className="blue-btn">
          Go back
        </Link>
      </div>
    </div>
  );
};

export default ItemPage;
