import React, { createContext, useContext, useState, FC, ReactNode, useEffect } from 'react';
import { IDoctor } from "../";
import doctorsData from '../../data.json';

const flattenedDoctorsData: IDoctor[] = doctorsData.flat().map(doctor => ({
    ...doctor,
    rating: 5,
    location: 'Ukraine'
}));

interface DoctorsContextProps {
    doctors: IDoctor[];
    setDoctors: React.Dispatch<React.SetStateAction<IDoctor[]>>;
    filters: { price: number; rating: number; country: string };
    setFilters: React.Dispatch<React.SetStateAction<{ price: number; rating: number; country: string }>>;
    filteredDoctors: IDoctor[];
}

const DoctorsContext = createContext<DoctorsContextProps | undefined>(undefined);

export const useDoctors = () => {
    const context = useContext(DoctorsContext);
    if (!context) {
        throw new Error('useDoctors must be used within a DoctorsProvider');
    }
    return context;
};

export const DoctorsProvider: FC<{ children: ReactNode }> = ({ children }) => {
    const [doctors, setDoctors] = useState<IDoctor[]>(flattenedDoctorsData);
    const [filters, setFilters] = useState<{ price: number; rating: number; country: string }>({ price: 0, rating: 0, country: '' });
    const [filteredDoctors, setFilteredDoctors] = useState<IDoctor[]>(doctors);

    useEffect(() => {
        const newFilteredDoctors = doctors.filter(doctor => {
            const matchesPrice = !filters.price || doctor.price <= filters.price;
            const matchesRating = !filters.rating || doctor.rating >= filters.rating;
            const matchesCountry = !filters.country || doctor.location.toLowerCase() === filters.country.toLowerCase();
            return matchesPrice && matchesRating && matchesCountry;
        });
        setFilteredDoctors(newFilteredDoctors);
    }, [doctors, filters]);

    return (
        <DoctorsContext.Provider value={{ doctors, setDoctors, filters, setFilters, filteredDoctors }}>
            {children}
        </DoctorsContext.Provider>
    );
};
