import React, { FC, useState } from 'react';
import SectionMenu from "../../features/SectionMenu/SectionMenu";
import SectionItems from "../../features/SectionItems/SectionItems";
import { useDoctors } from '../../context/DoctorsContext';
import './CatalogPage.scss';

const CatalogPage: FC = () => {
    const { doctors, setDoctors, searchOptions } = useDoctors();
    const [filteredDoctors, setFilteredDoctors] = useState<IDoctor[]>(doctors);
    const [visibleDoctors, setVisibleDoctors] = useState(10);

    useEffect(() => {
        const newFilteredDoctors = doctors.filter(doctor => {
            const matchesPrice = !searchOptions.price || doctor.price <= Number(searchOptions.price);
            const matchesRating = !searchOptions.rating || doctor.rating >= Number(searchOptions.rating);
            const matchesCountry = !searchOptions.country || doctor.location.toLowerCase() === searchOptions.country.toLowerCase();
            const matchesTerm = !searchOptions.term || 
                doctor.name.toLowerCase().includes(searchOptions.term.toLowerCase()) || 
                doctor.description.toLowerCase().includes(searchOptions.term.toLowerCase());
            return matchesPrice && matchesRating && matchesCountry && matchesTerm;
        });
        setFilteredDoctors(newFilteredDoctors);
    }, [doctors, searchOptions]);

    return (
        <div className="catalog-page">
            <SectionMenu />
            <SectionItems
                doctors={filteredDoctors.slice(0, visibleDoctors)}
                setDoctors={setDoctors}
            />
        </div>
    );
};

export default CatalogPage;
