import React, { FC, useState } from 'react';
import SectionMenu from "../../features/SectionMenu/SectionMenu";
import SectionItems from "../../features/SectionItems/SectionItems";
import { useDoctors } from '../../context/DoctorsContext';
import './CatalogPage.scss';

const CatalogPage: FC = () => {
    const { doctors, setDoctors } = useDoctors();
    const [visibleDoctors, setVisibleDoctors] = useState(3); // Початково відображаємо 3 лікарів
    const [searchOptions, setSearchOptions] = useState({
        term: '',
        sort: '',
        price: 0,
        rating: 0,
        country: ''
    });

    // Функція для збільшення кількості відображуваних лікарів
    const loadMoreDoctors = () => {
        setVisibleDoctors(prevVisible => prevVisible + 3);
    };

    return (
        <div className="catalog-page">
            <SectionMenu
                setSearchOptions={setSearchOptions}
            />
            <SectionItems
                doctors={doctors.slice(0, visibleDoctors)}
                setDoctors={setDoctors}
                searchOptions={searchOptions}
                setSearchOptions={setSearchOptions}
            />

            {/* Кнопка для завантаження більше лікарів */}
            {visibleDoctors < doctors.length && (
                <button onClick={loadMoreDoctors}>Load More</button>
            )}
        </div>
    );
};

export default CatalogPage;
