import React, { ChangeEvent, Dispatch, FC, FormEvent, SetStateAction } from 'react';
import PopUpModalWindow from "../../common/PopUpModalWindow/PopUpModalWindow";
import { IDoctor } from "../../../intefaces/doctorInterfaces";

// Якщо у вас немає компонента FormInput, використовуйте звичайний input
// import FormInput from "../../common/FormInput/FormInput";

interface PopUpDoctorFormProps {
    doctor: IDoctor;
    setDoctor: Dispatch<SetStateAction<IDoctor>>;
    handleSubmit: (e: FormEvent) => void;
    error: string;
    headText: string;
    active: boolean;
    setActive: Dispatch<SetStateAction<boolean>>;
}

const PopUpDoctorForm: FC<PopUpDoctorFormProps> = ({
    doctor,
    setDoctor,
    handleSubmit,
    error,
    headText,
    active,
    setActive
}) => {
    return (
        <PopUpModalWindow active={active} setActive={setActive}>
            <h2>{headText}</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input
                        value={doctor.name}
                        onChange={(e: ChangeEvent<HTMLInputElement>) => setDoctor({...doctor, name: e.target.value})}
                    />
                </label>
                <textarea
                    label="Description"
                    value={doctor.description}
                    onChange={(e: ChangeEvent<HTMLTextAreaElement>) => setDoctor({...doctor, description: e.target.value})}
                />
                <input
                    label="Price"
                    type="number"
                    value={doctor.price || ''}
                    onChange={(e: ChangeEvent<HTMLInputElement>) => setDoctor({...doctor, price: Number(e.target.value)})}
                />
                <input
                    label="Picture's url"
                    value={doctor.picture}
                    onChange={(e: ChangeEvent<HTMLInputElement>) => setDoctor({...doctor, picture: e.target.value})}
                />
                <span className='error'>{error}</span>
                <button className={'blue-btn small'} type={'submit'}>Добавити</button>
            </form>
        </PopUpModalWindow>
    );
};

export default PopUpDoctorForm;
