import React, { FC, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { useDoctors } from "../../context/DoctorsContext";
import starIcon from '../../../images/star.png';
import './ItemPage.scss';

const ItemPage: FC = () => {
    const { id } = useParams<{ id: string }>();
    const { doctors } = useDoctors();
    const doctor = doctors.find(d => d.doctor_id === Number(id));

    // Стан для контролю кількості відображуваних лікарів
    const [visibleDoctors, setVisibleDoctors] = useState(5); // Спочатку показуємо 5 лікарів

    // Функція для збільшення кількості відображуваних лікарів
    const loadMoreDoctors = () => {
        setVisibleDoctors(prev => prev + 5); // Додаємо ще 5 лікарів при кожному натисканні
    };

    if (!doctor) {
        return <div className='item-page'>Doctor not found</div>;
    }

    return (
        <div className='item-page'>
            <div className='info'>
                <img className='avatar' src={doctor.picture} alt={doctor.name} />
                <div className='details'>
                    <div className='filters'>
                        <div className='yellow'>
                            {doctor.rating}
                            <img width={20} height={20} src={starIcon} alt='stars' />
                        </div>
                        <div className='blue'>{doctor.location}</div>
                    </div>
                    <h1 className='h2'>{doctor.name}</h1>
                    <h2 className={'h7'}>{doctor.description}</h2>
                </div>
            </div>

            <div className='navigation'>
                <h5 className='h4'>Price: {doctor.price} $</h5>
                <Link to={'/catalog'} className='blue-btn small'>Go back</Link>
            </div>

            {/* Блок для відображення лікарів */}
            <div className="doctors-grid">
                {doctors.slice(0, visibleDoctors).map((doc) => (
                    <div className="doctor-card" key={doc.doctor_id}>
                        <img src={doc.picture} alt={doc.name} />
                        <h3>{doc.name}</h3>
                        <p>{doc.description}</p>
                        <Link to={`/doctor/${doc.doctor_id}`} className="view-btn">View Profile</Link>
                    </div>
                ))}
            </div>

            {/* Кнопка для завантаження більше лікарів */}
            {visibleDoctors < doctors.length && (
                <button className="load-more-btn" onClick={loadMoreDoctors}>
                    Load More
                </button>
            )}
        </div>
    );
};

export default ItemPage;
