import React, { FC, useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { useDoctors } from "../../context/DoctorsContext";
import starIcon from '../../../images/star.png';
import './ItemPage.scss';

const ItemPage: FC = () => {
    const { id } = useParams<{ id: string }>();
    const { doctors } = useDoctors();
    const [visibleDoctors, setVisibleDoctors] = useState<typeof doctors>([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const itemsPerLoad = 6;

    useEffect(() => {
        loadMoreDoctors();
    }, [doctors]);

    const loadMoreDoctors = () => {
        const nextIndex = currentIndex + itemsPerLoad;
        const newVisibleDoctors = doctors.slice(0, nextIndex);
        setVisibleDoctors(newVisibleDoctors);
        setCurrentIndex(nextIndex);
    };

    if (id) {
        const doctor = doctors.find(d => d.doctor_id === Number(id));

        if (!doctor) {
            return <div className='item-page'>Doctor not found</div>;
        }

        return (
            <div className='item-page'>
                <div className='info'>
                    <img className='avatar' src={doctor.picture} alt={doctor.name}/>
                    <div className='details'>
                        <div className='filters'>
                            <div className='yellow'>
                                {doctor.rating}
                                <img width={20} height={20} src={starIcon} alt='stars'/>
                            </div>
                            <div className='blue'>{doctor.location}</div>
                        </div>
                        <h1 className='h2'>{doctor.name}</h1>
                        <h2 className={'h7'}>{doctor.description}</h2>
                    </div>
                </div>

                <div className='navigation'>
                    <h5 className='h4'>Price: {doctor.price} $</h5>
                    <Link to={'/catalog'} className='blue-btn small'>Go back</Link>
                </div>
            </div>
        );
    }

    return (
        <div className='item-page'>
            <div className='doctors-list'>
                {visibleDoctors.map((doctor) => (
                    <Link to={`/doctor/${doctor.doctor_id}`} key={doctor.doctor_id} className='doctor-card'>
                        <img className='avatar' src={doctor.picture} alt={doctor.name}/>
                        <div className='details'>
                            <div className='filters'>
                                <div className='yellow'>
                                    {doctor.rating}
                                    <img width={20} height={20} src={starIcon} alt='stars'/>
                                </div>
                                <div className='blue'>{doctor.location}</div>
                            </div>
                            <h2 className='h2'>{doctor.name}</h2>
                            <p className='h7'>{doctor.description}</p>
                            <p className='h4'>Price: {doctor.price} $</p>
                        </div>
                    </Link>
                ))}
            </div>
            {currentIndex < doctors.length && (
                <button onClick={loadMoreDoctors} className='blue-btn small'>Load More</button>
            )}
        </div>
    );
};

export default ItemPage;
