import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ItemPage.scss';

interface Item {
  id: string;
  name: string;
  description: string;
  // Додайте інші необхідні поля
}

const ItemPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [item, setItem] = useState<Item | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchItem = async () => {
      setLoading(true);
      try {
        // Тут повинен бути реальний запит до API
        // Для прикладу використовуємо фіктивні дані
        await new Promise(resolve => setTimeout(resolve, 1000)); // Імітація затримки запиту

        if (id) {
          const fakeItem: Item = {
            id: id,
            name: `Item ${id}`,
            description: `This is the description for item ${id}.`,
          };
          setItem(fakeItem);
        } else {
          throw new Error('Item not found');
        }
      } catch (err) {
        setError('Failed to load item information');
      } finally {
        setLoading(false);
      }
    };

    fetchItem();
  }, [id]);

  if (loading) {
    return <div>Loading item information...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!item) {
    return <div>Item not found</div>;
  }

  return (
    <div className="item-page">
      <h1>{item.name}</h1>
      <p>{item.description}</p>
      {/* Додайте інші деталі товару */}
    </div>
  );
};

export default ItemPage;
