import React, { FC, useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { useDoctors } from "../../context/DoctorsContext";
import starIcon from '../../../images/star.png';
import './ItemPage.scss';

const ItemPage: FC = () => {
    const { id } = useParams<{ id: string }>();
    const { doctors } = useDoctors();
    const [visibleDoctors, setVisibleDoctors] = useState<typeof doctors>([]);
    const [currentIndex, setCurrentIndex] = useState(0);

    const doctor = doctors.find(d => d.doctor_id === Number(id));

    useEffect(() => {
        // Виключаємо поточного лікаря зі списку
        const otherDoctors = doctors.filter(d => d.doctor_id !== Number(id));
        setVisibleDoctors(otherDoctors.slice(0, 2));
        setCurrentIndex(2);
    }, [doctors, id]);

    if (!doctor) {
        return <div className='item-page'>Doctor not found</div>;
    }

    const loadMore = () => {
        const otherDoctors = doctors.filter(d => d.doctor_id !== Number(id));
        const newDoctors = otherDoctors.slice(currentIndex, currentIndex + 6);
        setVisibleDoctors(prevDoctors => [...prevDoctors, ...newDoctors]);
        setCurrentIndex(prevIndex => prevIndex + 6);
    };

    return (
        <div className='item-page'>
            <div className='info'>
                <img className='avatar' src={doctor.picture} alt={doctor.name}/>
                <div className='details'>
                    <div className='filters'>
                        <div className='yellow'>
                            {doctor.rating}
                            <img width={20} height={20} src={starIcon} alt='stars'/>
                        </div>
                        <div className='blue'>{doctor.location}</div>
                    </div>
                    <h1 className='h2'>{doctor.name}</h1>
                    <h2 className={'h7'}>{doctor.description}</h2>
                </div>
            </div>

            <div className='navigation'>
                <h5 className='h4'>Price: {doctor.price} $</h5>
                <Link to={'/catalog'} className='blue-btn small'>Go back</Link>
            </div>

            <h2>Other Doctors</h2>
            <div className='doctors-grid'>
                {visibleDoctors.map(doc => (
                    <div key={doc.doctor_id} className='doctor-card'>
                        <img src={doc.picture} alt={doc.name} />
                        <h3>{doc.name}</h3>
                        <p>{doc.description}</p>
                        <p>Price: {doc.price} $</p>
                        <Link to={`/catalog/${doc.doctor_id}`} className='view-btn'>View Profile</Link>
                    </div>
                ))}
            </div>

            {currentIndex < doctors.length - 1 && (
                <button onClick={loadMore} className='load-more-btn'>Load More</button>
            )}
        </div>
    );
};

export default ItemPage;
